@push('scripts')
    <script>
        // Partner Logo Animation Fix
        // Partner Logo Animation Fix
        (function() {
            // Utility function to check if DOM is loaded
            function domReady(callback) {
                if (document.readyState === 'loading') {
                    document.addEventListener('DOMContentLoaded', callback);
                } else {
                    callback();
                }
            }

            // Initialize the animation
            domReady(function() {
                // We need to wait for webflow.js to initialize
                setTimeout(initPartnerAnimation, 1000);
            });

            function initPartnerAnimation() {
                // Find the partner wrapper
                const partnerWrapper = document.querySelector('[data-w-id="61b768d7-9945-e866-e657-60a03f238781"]');

                if (!partnerWrapper) {
                    return;
                }


                // Find all partner component grids
                const partnerGrids = partnerWrapper.querySelectorAll('.partner-component-grid');

                if (partnerGrids.length === 0) {
                    console.log('No partner grids found');
                    return;
                }

                // Fix styling for animation
                partnerWrapper.style.display = 'flex';
                partnerWrapper.style.overflowX = 'hidden';
                partnerWrapper.style.position = 'relative';

                // Reset grid transforms
                partnerGrids.forEach(grid => {
                    grid.style.transform = 'translate3d(0, 0, 0)';
                    grid.style.willChange = 'transform';
                    grid.style.flex = '0 0 auto';
                });

                // Calculate animation parameters
                const totalWidth = Array.from(partnerGrids).reduce((total, grid) => {
                    return total + grid.offsetWidth;
                }, 0);

                let animationStartTime = null;
                const animationDuration = 60000;

                // Animation function
                function animatePartners(timestamp) {
                    if (!animationStartTime) {
                        animationStartTime = timestamp;
                    }

                    const elapsed = timestamp - animationStartTime;
                    const progress = (elapsed % animationDuration) / animationDuration;

                    // Calculate position for the animation
                    // We want to continuously move from right to left
                    const position = -progress * totalWidth;

                    // Apply the transforms
                    partnerGrids.forEach(grid => {
                        // Calculate the position for each grid
                        grid.style.transform = `translate3d(${position}px, 0, 0)`;
                    });

                    // If we've gone past the width of the first grid, reset
                    if (Math.abs(position) >= partnerGrids[0].offsetWidth) {
                        // Move the first grid to the end
                        const firstGrid = partnerGrids[0];
                        partnerWrapper.appendChild(firstGrid);

                        // Reset the animation start time
                        animationStartTime = timestamp;
                    }

                    // Continue animation
                    requestAnimationFrame(animatePartners);
                }

                // Start the animation
                requestAnimationFrame(animatePartners);
            }

        })();
    </script>
@endpush
